    private final boolean mHasChinaCollator;

    public static class Token {
        public static final int LATIN = 1;
        public static final int PINYIN = 2;
        public static final String SEPARATOR = " ";
        public static final int UNKNOWN = 3;
        public String source;
        public String target;
        public int type;

        public Token(int type, String source, String target) {
            this.type = type;
            this.source = source;
            this.target = target;
        }
    }

    protected HanziToPinyin(boolean hasChinaCollator) {
        this.mHasChinaCollator = hasChinaCollator;
    }

    public static HanziToPinyin getInstance() {
        synchronized (HanziToPinyin.class) {
            HanziToPinyin hanziToPinyin;
            if (sInstance != null) {
                hanziToPinyin = sInstance;
                return hanziToPinyin;
            }
            Locale[] locale = Collator.getAvailableLocales();
            int i = 0;
            while (i < locale.length) {
                if (locale[i].equals(Locale.CHINESE) || locale[i].equals(Locale.CHINA) || locale[i].toString().equals(zh_Hans_CN)) {
                    sInstance = new HanziToPinyin(true);
                    hanziToPinyin = sInstance;
                    return hanziToPinyin;
                }
                i++;
            }
            Log.w(TAG, "There is no Chinese collator, HanziToPinyin is disabled");
            sInstance = new HanziToPinyin(false);
            hanziToPinyin = sInstance;
            return hanziToPinyin;
        }
    }

    private Token getToken(char character) {
        Token token = new Token();
        String letter = Character.toString(character);
        token.source = letter;
        int offset = -1;
        if (character < 256) {
            token.type = 1;
            token.target = letter;
            return token;
        } else if (character < FIRST_UNIHAN) {
            token.type = 3;
            token.target = letter;
            return token;
        } else {
            int cmp = COLLATOR.compare(letter, FIRST_PINYIN_UNIHAN);
            if (cmp < 0) {
                token.type = 3;
                token.target = letter;
                return token;
            }
            if (cmp == 0) {
                token.type = 2;
                offset = 0;
            } else {
                cmp = COLLATOR.compare(letter, LAST_PINYIN_UNIHAN);
                if (cmp > 0) {
                    token.type = 3;
                    token.target = letter;
                    return token;
                } else if (cmp == 0) {
                    token.type = 2;
                    offset = UNIHANS.length - 1;
                }
            }
            token.type = 2;
            String mMulStr = getMulPinyin(letter);
            if (mMulStr != null) {
                token.target = mMulStr;
                return token;
            }
            if (offset < 0) {
                int begin = 0;
                int end = UNIHANS.length - 1;
                while (begin <= end) {
                    offset = (begin + end) / 2;
                    cmp = COLLATOR.compare(letter, Character.toString(UNIHANS[offset]));
                    if (cmp == 0) {
                        break;
                    } else if (cmp > 0) {
                        begin = offset + 1;
                    } else {
                        end = offset - 1;
                    }
                }
            }
            if (cmp < 0) {
                offset--;
            }
            StringBuilder pinyin = new StringBuilder();
            if (offset >= 0 && offset < PINYINS.length) {
                int j = 0;
                while (j < PINYINS[offset].length && PINYINS[offset][j] != (byte) 0) {
                    pinyin.append((char) PINYINS[offset][j]);
                    j++;
                }
            }
            token.target = pinyin.toString();
            return token;
        }
    }

    public ArrayList<Token> get(String input) {
        ArrayList<Token> tokens = new ArrayList();
        if (!this.mHasChinaCollator || TextUtils.isEmpty(input)) {
            return tokens;
        }
        int inputLength = input.length();
        StringBuilder sb = new StringBuilder();
        int tokenType = 1;
        for (int i = 0; i < inputLength; i++) {
            char character = input.charAt(i);
            if (character == ' ') {
                if (sb.length() > 0) {
                    addToken(sb, tokens, tokenType);
                }
            } else if (character < 256) {
                if (tokenType != 1 && sb.length() > 0) {
                    addToken(sb, tokens, tokenType);
                }
                tokenType = 1;
                if ((character <= 'Z' || character >= 'a') && character <= 'z') {
                    sb.append(character);
                } else {
                    sb.append('@');
                }
            } else if (character < FIRST_UNIHAN) {
                if (tokenType != 3 && sb.length() > 0) {
                    addToken(sb, tokens, tokenType);
                }
                tokenType = 3;
                sb.append(character);
            } else {
                Token t = getToken(character);
                if (t.type == 2) {
                    if (sb.length() > 0) {
                        addToken(sb, tokens, tokenType);
                    }
                    tokens.add(t);
                    tokenType = 2;
                } else {
                    if (tokenType != t.type && sb.length() > 0) {
                        addToken(sb, tokens, tokenType);
                    }
                    tokenType = t.type;
                    sb.append(character);
                }
            }
        }
        if (sb.length() > 0) {
            addToken(sb, tokens, tokenType);
        }
        return tokens;
    }

    private void addToken(StringBuilder sb, ArrayList<Token> tokens, int tokenType) {
        String str = sb.toString();
        tokens.add(new Token(tokenType, str, str));
        sb.setLength(0);
    }

    public static String getFullPinyinHasSpace(String displayName) {
        ArrayList<Token> tokens = getInstance().get(displayName);
        if (tokens == null || tokens.size() <= 0) {
            return displayName;
        }
        StringBuilder sb = new StringBuilder();
        for (Token token : tokens) {
            if (2 == token.type) {
                if (sb.length() > 0) {
                    sb.append(' ');
                }
                sb.append(token.target);
            } else if (3 == token.type) {
                sb.append('@');
            } else {
                if (sb.length() > 0) {
                    sb.append(' ');
                }
                sb.append(token.source);
            }
        }
        return sb.toString();
    }

